import React, { useState } from "react";
import Card from "../UI/Card";
import "./FormInput.css";
import Popup from 'react-popup';



const FormInput = () => {

    const [alert, setAlert] = React.useState({
        type: 'error',
        text: 'This is a alert message',
        show: false
    })

    function onShowAlert(type) {
        setAlert({
            type: type,
            text: 'Demo alert',
            show: true
        })}

    const [name, setName] = useState("")
        const [age, setAge] = useState("")

        const nomeChangeHandler = (event) => {
            setName(event.target.value)
        }

        const idadeChangeHandler = (event) => {
            setAge(event.target.value)
        }

        const submitHandler = (event) => {
            event.preventDefault();
            if (!/^[a-zA-Z]+$/.test(event.nome) && event.age > 0) {
                const dataForm = {
                    id: Math.floor(Math.random() * 10000),
                    nome: name,
                    idade: age

                }
                console.log(dataForm)
            } else
            Popup.alert('I am alert, nice to meet you');
        }




        return (
            <Card>
                <form onSubmit={submitHandler} className="nova-pessoa">
                    <label>Nome</label>
                    <input type="text" value={name} onChange={nomeChangeHandler}></input>
                    <label>Idade</label>
                    <input type="number" value={age} onChange={idadeChangeHandler}></input>
                    <button type="submit">Enviar</button>
                </form>
            </Card>
        );
    }

    export default FormInput;
